import{_ as e,o as a,c as s,d as p}from"./app.af5ffdc4.js";const r="/assets/227fe770-dbff-40ec-a34f-88950837a5ae.ea4cf8a8.png",t={},d=p('<h1 id="elasticsearch-es-\u5206\u5E03\u5F0F\u67B6\u6784\u539F\u7406" tabindex="-1"><a class="header-anchor" href="#elasticsearch-es-\u5206\u5E03\u5F0F\u67B6\u6784\u539F\u7406" aria-hidden="true">#</a> Elasticsearch\uFF08ES\uFF09\u5206\u5E03\u5F0F\u67B6\u6784\u539F\u7406</h1><p><img src="'+r+'" alt="elasticsearch\u5206\u5E03\u5F0F\u67B6\u6784\u539F\u7406"></p><h3 id="_1\u3001\u9762\u8BD5\u9898" tabindex="-1"><a class="header-anchor" href="#_1\u3001\u9762\u8BD5\u9898" aria-hidden="true">#</a> 1\u3001\u9762\u8BD5\u9898</h3><p>es\u7684\u5206\u5E03\u5F0F\u67B6\u6784\u539F\u7406\u80FD\u8BF4\u4E00\u4E0B\u4E48\uFF08es\u662F\u5982\u4F55\u5B9E\u73B0\u5206\u5E03\u5F0F\u7684\u554A\uFF09\uFF1F</p><h3 id="_2\u3001\u9762\u8BD5\u5B98\u5FC3\u91CC\u5206\u6790" tabindex="-1"><a class="header-anchor" href="#_2\u3001\u9762\u8BD5\u5B98\u5FC3\u91CC\u5206\u6790" aria-hidden="true">#</a> 2\u3001\u9762\u8BD5\u5B98\u5FC3\u91CC\u5206\u6790</h3><p>\u5728\u641C\u7D22\u8FD9\u5757\uFF0Clucene\u662F\u6700\u6D41\u884C\u7684\u641C\u7D22\u5E93\u3002\u51E0\u5E74\u524D\u4E1A\u5185\u4E00\u822C\u90FD\u95EE\uFF0C\u4F60\u4E86\u89E3lucene\u5417\uFF1F\u4F60\u77E5\u9053\u5012\u6392\u7D22\u5F15\u7684\u539F\u7406\u5417\uFF1F\u73B0\u5728\u65E9\u5DF2\u7ECFout\u4E86\uFF0C\u56E0\u4E3A\u73B0\u5728\u5F88\u591A\u9879\u76EE\u90FD\u662F\u76F4\u63A5\u7528\u57FA\u4E8Elucene\u7684\u5206\u5E03\u5F0F\u641C\u7D22\u5F15\u64CE\u2014\u2014elasticsearch\uFF0C\u7B80\u79F0\u4E3Aes\u3002</p><p>\u800C\u73B0\u5728\u5206\u5E03\u5F0F\u641C\u7D22\u57FA\u672C\u5DF2\u7ECF\u6210\u4E3A\u5927\u90E8\u5206\u4E92\u8054\u7F51\u884C\u4E1A\u7684java\u7CFB\u7EDF\u7684\u6807\u914D\uFF0C\u5176\u4E2D\u5C24\u4E3A\u6D41\u884C\u7684\u5C31\u662Fes\uFF0C\u524D\u51E0\u5E74es\u6CA1\u706B\u7684\u65F6\u5019\uFF0C\u5927\u5BB6\u4E00\u822C\u7528solr\u3002\u4F46\u662F\u8FD9\u4E24\u5E74\u57FA\u672C\u5927\u90E8\u5206\u4F01\u4E1A\u548C\u9879\u76EE\u90FD\u5F00\u59CB\u8F6C\u5411es\u4E86\u3002</p><p>\u6240\u4EE5\u4E92\u8054\u7F51\u9762\u8BD5\uFF0C\u80AF\u5B9A\u4F1A\u8DDF\u4F60\u804A\u804A\u5206\u5E03\u5F0F\u641C\u7D22\u5F15\u64CE\uFF0C\u4E5F\u5C31\u4E00\u5B9A\u4F1A\u804A\u804Aes\uFF0C\u5982\u679C\u4F60\u786E\u5B9E\u4E0D\u77E5\u9053\uFF0C\u90A3\u4F60\u771F\u7684\u5C31out\u4E86\u3002</p><p>\u5982\u679C\u9762\u8BD5\u5B98\u95EE\u4F60\u7B2C\u4E00\u4E2A\u95EE\u9898\uFF0C\u786E\u5B9E\u4E00\u822C\u90FD\u4F1A\u95EE\u4F60es\u7684\u5206\u5E03\u5F0F\u67B6\u6784\u8BBE\u8BA1\u80FD\u4ECB\u7ECD\u4E00\u4E0B\u4E48\uFF1F\u5C31\u770B\u770B\u4F60\u5BF9\u5206\u5E03\u5F0F\u641C\u7D22\u5F15\u64CE\u67B6\u6784\u7684\u4E00\u4E2A\u57FA\u672C\u7406\u89E3\u3002</p><h3 id="_3\u3001\u989D\u5916\u7684\u53CB\u60C5\u63D0\u793A" tabindex="-1"><a class="header-anchor" href="#_3\u3001\u989D\u5916\u7684\u53CB\u60C5\u63D0\u793A" aria-hidden="true">#</a> 3\u3001\u989D\u5916\u7684\u53CB\u60C5\u63D0\u793A</h3><p>\u540C\u5B66\u554A\uFF0C\u5982\u679C\u4F60\u770B\u5230\u8FD9\u91CC\u53D1\u73B0\u81EA\u5DF1\u5BF9es\u4E00\u65E0\u6240\u77E5\uFF0C\u6CA1\u4E8B\u513F\uFF0C\u4FDD\u6301\u6DE1\u5B9A\uFF0C\u6682\u505C\u4E00\u4E0B\u8BFE\u7A0B\u3002\u7136\u540E\u4E0A\u767E\u5EA6\u641C\u4E00\u4E0Bes\u662F\u5565\uFF1F\u672C\u673A\u542F\u52A8\u4E2Aes\uFF1F\u7136\u540E\u5199\u4E2Aes\u7684hello world\u611F\u53D7\u4E00\u4E0B\uFF1F\u7136\u540E\u641C\u4E2A\u5E16\u5B50\u628Aes\u5E38\u89C1\u7684\u51E0\u4E2A\u64CD\u4F5C\u90FD\u6267\u884C\u4E00\u904D\uFF08\u805A\u5408\u3001\u5E38\u89C1\u641C\u7D22\u8BED\u6CD5\u4E4B\u7C7B\u7684\uFF09\uFF1Fok\u4E86\uFF0C1~2\u5C0F\u65F6\u719F\u6089\u8DB3\u591F\u4E86\uFF0C\u56DE\u6765\u5427\uFF0C\u7EE7\u7EED\u770B\u6211\u4EEC\u7684\u8BFE\u7A0B\u3002</p><h3 id="_4\u3001\u9762\u8BD5\u9898\u5256\u6790" tabindex="-1"><a class="header-anchor" href="#_4\u3001\u9762\u8BD5\u9898\u5256\u6790" aria-hidden="true">#</a> 4\u3001\u9762\u8BD5\u9898\u5256\u6790</h3><p>elasticsearch\u8BBE\u8BA1\u7684\u7406\u5FF5\u5C31\u662F\u5206\u5E03\u5F0F\u641C\u7D22\u5F15\u64CE\uFF0C\u5E95\u5C42\u5176\u5B9E\u8FD8\u662F\u57FA\u4E8Elucene\u7684\u3002</p><p>\u6838\u5FC3\u601D\u60F3\u5C31\u662F\u5728\u591A\u53F0\u673A\u5668\u4E0A\u542F\u52A8\u591A\u4E2Aes\u8FDB\u7A0B\u5B9E\u4F8B\uFF0C\u7EC4\u6210\u4E86\u4E00\u4E2Aes\u96C6\u7FA4\u3002</p><p>es\u4E2D\u5B58\u50A8\u6570\u636E\u7684\u57FA\u672C\u5355\u4F4D\u662F\u7D22\u5F15\uFF0C\u6BD4\u5982\u8BF4\u4F60\u73B0\u5728\u8981\u5728es\u4E2D\u5B58\u50A8\u4E00\u4E9B\u8BA2\u5355\u6570\u636E\uFF0C\u4F60\u5C31\u5E94\u8BE5\u5728es\u4E2D\u521B\u5EFA\u4E00\u4E2A\u7D22\u5F15\uFF0Corder_idx\uFF0C\u6240\u6709\u7684\u8BA2\u5355\u6570\u636E\u5C31\u90FD\u5199\u5230\u8FD9\u4E2A\u7D22\u5F15\u91CC\u9762\u53BB\uFF0C\u4E00\u4E2A\u7D22\u5F15\u5DEE\u4E0D\u591A\u5C31\u662F\u76F8\u5F53\u4E8E\u662Fmysql\u91CC\u7684\u4E00\u5F20\u8868\u3002index -&gt; type -&gt; mapping -&gt; document -&gt; field\u3002</p><p>index\uFF1Amysql\u91CC\u7684\u4E00\u5F20\u8868</p><p>type\uFF1A\u6CA1\u6CD5\u8DDFmysql\u91CC\u53BB\u5BF9\u6BD4\uFF0C\u4E00\u4E2Aindex\u91CC\u53EF\u4EE5\u6709\u591A\u4E2Atype\uFF0C\u6BCF\u4E2Atype\u7684\u5B57\u6BB5\u90FD\u662F\u5DEE\u4E0D\u591A\u7684\uFF0C\u4F46\u662F\u6709\u4E00\u4E9B\u7565\u5FAE\u7684\u5DEE\u522B\u3002</p><p>\u597D\u6BD4\u8BF4\uFF0C\u6709\u4E00\u4E2Aindex\uFF0C\u662F\u8BA2\u5355index\uFF0C\u91CC\u9762\u4E13\u95E8\u662F\u653E\u8BA2\u5355\u6570\u636E\u7684\u3002\u5C31\u597D\u6BD4\u8BF4\u4F60\u5728mysql\u4E2D\u5EFA\u8868\uFF0C\u6709\u4E9B\u8BA2\u5355\u662F\u5B9E\u7269\u5546\u54C1\u7684\u8BA2\u5355\uFF0C\u5C31\u597D\u6BD4\u8BF4\u4E00\u4EF6\u8863\u670D\uFF0C\u4E00\u53CC\u978B\u5B50\uFF1B\u6709\u4E9B\u8BA2\u5355\u662F\u865A\u62DF\u5546\u54C1\u7684\u8BA2\u5355\uFF0C\u5C31\u597D\u6BD4\u8BF4\u6E38\u620F\u70B9\u5361\uFF0C\u8BDD\u8D39\u5145\u503C\u3002\u5C31\u4E24\u79CD\u8BA2\u5355\u5927\u90E8\u5206\u5B57\u6BB5\u662F\u4E00\u6837\u7684\uFF0C\u4F46\u662F\u5C11\u90E8\u5206\u5B57\u6BB5\u53EF\u80FD\u6709\u7565\u5FAE\u7684\u4E00\u4E9B\u5DEE\u522B\u3002</p><p>\u6240\u4EE5\u5C31\u4F1A\u5728\u8BA2\u5355index\u91CC\uFF0C\u5EFA\u4E24\u4E2Atype\uFF0C\u4E00\u4E2A\u662F\u5B9E\u7269\u5546\u54C1\u8BA2\u5355type\uFF0C\u4E00\u4E2A\u662F\u865A\u62DF\u5546\u54C1\u8BA2\u5355type\uFF0C\u8FD9\u4E24\u4E2Atype\u5927\u90E8\u5206\u5B57\u6BB5\u662F\u4E00\u6837\u7684\uFF0C\u5C11\u90E8\u5206\u5B57\u6BB5\u662F\u4E0D\u4E00\u6837\u7684\u3002</p><p>\u5F88\u591A\u60C5\u51B5\u4E0B\uFF0C\u4E00\u4E2Aindex\u91CC\u53EF\u80FD\u5C31\u4E00\u4E2Atype\uFF0C\u4F46\u662F\u786E\u5B9E\u5982\u679C\u8BF4\u662F\u4E00\u4E2Aindex\u91CC\u6709\u591A\u4E2Atype\u7684\u60C5\u51B5\uFF0C\u4F60\u53EF\u4EE5\u8BA4\u4E3Aindex\u662F\u4E00\u4E2A\u7C7B\u522B\u7684\u8868\uFF0C\u5177\u4F53\u7684\u6BCF\u4E2Atype\u4EE3\u8868\u4E86\u5177\u4F53\u7684\u4E00\u4E2Amysql\u4E2D\u7684\u8868</p><p>\u6BCF\u4E2Atype\u6709\u4E00\u4E2Amapping\uFF0C\u5982\u679C\u4F60\u8BA4\u4E3A\u4E00\u4E2Atype\u662F\u4E00\u4E2A\u5177\u4F53\u7684\u4E00\u4E2A\u8868\uFF0Cindex\u4EE3\u8868\u4E86\u591A\u4E2Atype\u7684\u540C\u5C5E\u4E8E\u7684\u4E00\u4E2A\u7C7B\u578B\uFF0Cmapping\u5C31\u662F\u8FD9\u4E2Atype\u7684\u8868\u7ED3\u6784\u5B9A\u4E49\uFF0C\u4F60\u5728mysql\u4E2D\u521B\u5EFA\u4E00\u4E2A\u8868\uFF0C\u80AF\u5B9A\u662F\u8981\u5B9A\u4E49\u8868\u7ED3\u6784\u7684\uFF0C\u91CC\u9762\u6709\u54EA\u4E9B\u5B57\u6BB5\uFF0C\u6BCF\u4E2A\u5B57\u6BB5\u662F\u4EC0\u4E48\u7C7B\u578B\u3002\u3002\u3002</p><p>mapping\u5C31\u4EE3\u8868\u4E86\u8FD9\u4E2Atype\u7684\u8868\u7ED3\u6784\u7684\u5B9A\u4E49\uFF0C\u5B9A\u4E49\u4E86\u8FD9\u4E2Atype\u4E2D\u6BCF\u4E2A\u5B57\u6BB5\u540D\u79F0\uFF0C\u5B57\u6BB5\u662F\u4EC0\u4E48\u7C7B\u578B\u7684\uFF0C\u7136\u540E\u8FD8\u6709\u8FD9\u4E2A\u5B57\u6BB5\u7684\u5404\u79CD\u914D\u7F6E</p><p>\u5B9E\u9645\u4E0A\u4F60\u5F80index\u91CC\u7684\u4E00\u4E2Atype\u91CC\u9762\u5199\u7684\u4E00\u6761\u6570\u636E\uFF0C\u53EB\u505A\u4E00\u6761document\uFF0C\u4E00\u6761document\u5C31\u4EE3\u8868\u4E86mysql\u4E2D\u67D0\u4E2A\u8868\u91CC\u7684\u4E00\u884C\u7ED9\uFF0C\u6BCF\u4E2Adocument\u6709\u591A\u4E2Afield\uFF0C\u6BCF\u4E2Afield\u5C31\u4EE3\u8868\u4E86\u8FD9\u4E2Adocument\u4E2D\u7684\u4E00\u4E2A\u5B57\u6BB5\u7684\u503C</p><p>\u63A5\u7740\u4F60\u641E\u4E00\u4E2A\u7D22\u5F15\uFF0C\u8FD9\u4E2A\u7D22\u5F15\u53EF\u4EE5\u62C6\u5206\u6210\u591A\u4E2Ashard\uFF0C\u6BCF\u4E2Ashard\u5B58\u50A8\u90E8\u5206\u6570\u636E\u3002</p><p>\u63A5\u7740\u5C31\u662F\u8FD9\u4E2Ashard\u7684\u6570\u636E\u5B9E\u9645\u662F\u6709\u591A\u4E2A\u5907\u4EFD\uFF0C\u5C31\u662F\u8BF4\u6BCF\u4E2Ashard\u90FD\u6709\u4E00\u4E2Aprimary shard\uFF0C\u8D1F\u8D23\u5199\u5165\u6570\u636E\uFF0C\u4F46\u662F\u8FD8\u6709\u51E0\u4E2Areplica shard\u3002primary shard\u5199\u5165\u6570\u636E\u4E4B\u540E\uFF0C\u4F1A\u5C06\u6570\u636E\u540C\u6B65\u5230\u5176\u4ED6\u51E0\u4E2Areplica shard\u4E0A\u53BB\u3002</p><p>\u901A\u8FC7\u8FD9\u4E2Areplica\u7684\u65B9\u6848\uFF0C\u6BCF\u4E2Ashard\u7684\u6570\u636E\u90FD\u6709\u591A\u4E2A\u5907\u4EFD\uFF0C\u5982\u679C\u67D0\u4E2A\u673A\u5668\u5B95\u673A\u4E86\uFF0C\u6CA1\u5173\u7CFB\u554A\uFF0C\u8FD8\u6709\u522B\u7684\u6570\u636E\u526F\u672C\u5728\u522B\u7684\u673A\u5668\u4E0A\u5462\u3002\u9AD8\u53EF\u7528\u4E86\u5427\u3002</p><p>es\u96C6\u7FA4\u591A\u4E2A\u8282\u70B9\uFF0C\u4F1A\u81EA\u52A8\u9009\u4E3E\u4E00\u4E2A\u8282\u70B9\u4E3Amaster\u8282\u70B9\uFF0C\u8FD9\u4E2Amaster\u8282\u70B9\u5176\u5B9E\u5C31\u662F\u5E72\u4E00\u4E9B\u7BA1\u7406\u7684\u5DE5\u4F5C\u7684\uFF0C\u6BD4\u5982\u7EF4\u62A4\u7D22\u5F15\u5143\u6570\u636E\u62C9\uFF0C\u8D1F\u8D23\u5207\u6362primary shard\u548Creplica shard\u8EAB\u4EFD\u62C9\uFF0C\u4E4B\u7C7B\u7684\u3002</p><p>\u8981\u662Fmaster\u8282\u70B9\u5B95\u673A\u4E86\uFF0C\u90A3\u4E48\u4F1A\u91CD\u65B0\u9009\u4E3E\u4E00\u4E2A\u8282\u70B9\u4E3Amaster\u8282\u70B9\u3002</p><p>\u5982\u679C\u662F\u975Emaster\u8282\u70B9\u5B95\u673A\u4E86\uFF0C\u90A3\u4E48\u4F1A\u7531master\u8282\u70B9\uFF0C\u8BA9\u90A3\u4E2A\u5B95\u673A\u8282\u70B9\u4E0A\u7684primary shard\u7684\u8EAB\u4EFD\u8F6C\u79FB\u5230\u5176\u4ED6\u673A\u5668\u4E0A\u7684replica shard\u3002\u6025\u7740\u4F60\u8981\u662F\u4FEE\u590D\u4E86\u90A3\u4E2A\u5B95\u673A\u673A\u5668\uFF0C\u91CD\u542F\u4E86\u4E4B\u540E\uFF0Cmaster\u8282\u70B9\u4F1A\u63A7\u5236\u5C06\u7F3A\u5931\u7684replica shard\u5206\u914D\u8FC7\u53BB\uFF0C\u540C\u6B65\u540E\u7EED\u4FEE\u6539\u7684\u6570\u636E\u4E4B\u7C7B\u7684\uFF0C\u8BA9\u96C6\u7FA4\u6062\u590D\u6B63\u5E38\u3002</p><p>\u5176\u5B9E\u4E0A\u8FF0\u5C31\u662Felasticsearch\u4F5C\u4E3A\u4E00\u4E2A\u5206\u5E03\u5F0F\u641C\u7D22\u5F15\u64CE\u6700\u57FA\u672C\u7684\u4E00\u4E2A\u67B6\u6784\u8BBE\u8BA1</p>',30),i=[d];function c(h,n){return a(),s("div",null,i)}const _=e(t,[["render",c],["__file","Elasticsearch\uFF08ES\uFF09\u5206\u5E03\u5F0F\u67B6\u6784\u539F\u7406.html.vue"]]);export{_ as default};
