import{_ as e,o as t,c as i,d as a}from"./app.af5ffdc4.js";const r={},o=a('<h1 id="\u591A\u7EBF\u7A0B\u7F16\u7A0B\u4E2D\u7684\u4E0A\u4E0B\u6587\u5207\u6362" tabindex="-1"><a class="header-anchor" href="#\u591A\u7EBF\u7A0B\u7F16\u7A0B\u4E2D\u7684\u4E0A\u4E0B\u6587\u5207\u6362" aria-hidden="true">#</a> \u591A\u7EBF\u7A0B\u7F16\u7A0B\u4E2D\u7684\u4E0A\u4E0B\u6587\u5207\u6362</h1><h2 id="_1-\u4EC0\u4E48\u662F\u4E0A\u4E0B\u6587\u5207\u6362" tabindex="-1"><a class="header-anchor" href="#_1-\u4EC0\u4E48\u662F\u4E0A\u4E0B\u6587\u5207\u6362" aria-hidden="true">#</a> 1 \u4EC0\u4E48\u662F\u4E0A\u4E0B\u6587\u5207\u6362</h2><p><strong>\u591A\u7EBF\u7A0B</strong> \u7F16\u7A0B\u4E2D\u4E00\u822C\u7EBF\u7A0B\u7684\u4E2A\u6570\u90FD\u5927\u4E8E CPU \u6838\u5FC3\u7684\u4E2A\u6570\uFF0C\u800C\u4E00\u4E2A CPU \u6838\u5FC3\u5728\u4EFB\u610F\u65F6\u523B\u53EA\u80FD\u88AB\u4E00\u4E2A\u7EBF\u7A0B\u4F7F\u7528\uFF0C\u4E3A\u4E86\u8BA9\u8FD9\u4E9B\u7EBF\u7A0B\u90FD\u80FD\u5F97\u5230\u6709\u6548\u6267\u884C\uFF0CCPU \u91C7\u53D6\u7684\u7B56\u7565\u662F<strong>\u4E3A\u6BCF\u4E2A\u7EBF\u7A0B\u5206\u914D\u65F6\u95F4\u7247\u5E76\u8F6E\u8F6C\u7684\u5F62\u5F0F</strong>\u3002\u5F53\u4E00\u4E2A\u7EBF\u7A0B\u7684\u65F6\u95F4\u7247\u7528\u5B8C\u7684\u65F6\u5019\u5C31\u4F1A\u91CD\u65B0\u5904\u4E8E\u5C31\u7EEA\u72B6\u6001\u8BA9\u7ED9\u5176\u4ED6\u7EBF\u7A0B\u4F7F\u7528\uFF0C\u8FD9\u4E2A\u8FC7\u7A0B\u5C31\u5C5E\u4E8E\u4E00\u6B21\u4E0A\u4E0B\u6587\u5207\u6362\u3002</p><blockquote><p>\u5373\u4F7F\u662F\u5355\u6838CPU\u4E5F\u652F\u6301\u591A\u7EBF\u7A0B\u6267\u884C\u4EE3\u7801\uFF0CCPU\u901A\u8FC7\u7ED9\u6BCF\u4E2A\u7EBF\u7A0B\u5206\u914DCPU\u65F6\u95F4\u7247\u6765\u5B9E\u73B0\u8FD9\u4E2A\u673A\u5236\u3002\u65F6\u95F4\u7247\u662FCPU\u5206\u914D\u7ED9\u5404\u4E2A\u7EBF\u7A0B\u7684\u65F6\u95F4\uFF0C\u56E0\u4E3A\u65F6\u95F4\u7247\u975E\u5E38\u77ED\uFF0C\u6240\u4EE5CPU\u901A\u8FC7\u4E0D\u505C\u5730\u5207\u6362\u7EBF\u7A0B\u6267\u884C\uFF0C\u8BA9\u6211\u4EEC\u611F\u89C9\u591A\u4E2A\u7EBF\u7A0B\u65F6\u540C\u65F6\u6267\u884C\u7684\uFF0C\u65F6\u95F4\u7247\u4E00\u822C\u662F\u51E0\u5341\u6BEB\u79D2\uFF08ms\uFF09\u3002</p></blockquote><blockquote><p>\u4E0A\u4E0B\u6587\u5207\u6362 (context switch) , \u5176\u5B9E\u9645\u542B\u4E49\u662F\u4EFB\u52A1\u5207\u6362, \u6216\u8005CPU\u5BC4\u5B58\u5668\u5207\u6362\u3002\u5F53\u591A\u4EFB\u52A1\u5185\u6838\u51B3\u5B9A\u8FD0\u884C\u53E6\u5916\u7684\u4EFB\u52A1\u65F6, \u5B83\u4FDD\u5B58\u6B63\u5728\u8FD0\u884C\u4EFB\u52A1\u7684\u5F53\u524D\u72B6\u6001, \u4E5F\u5C31\u662FCPU\u5BC4\u5B58\u5668\u4E2D\u7684\u5168\u90E8\u5185\u5BB9\u3002\u8FD9\u4E9B\u5185\u5BB9\u88AB\u4FDD\u5B58\u5728\u4EFB\u52A1\u81EA\u5DF1\u7684\u5806\u6808\u4E2D, \u5165\u6808\u5DE5\u4F5C\u5B8C\u6210\u540E\u5C31\u628A\u4E0B\u4E00\u4E2A\u5C06\u8981\u8FD0\u884C\u7684\u4EFB\u52A1\u7684\u5F53\u524D\u72B6\u51B5\u4ECE\u8BE5\u4EFB\u52A1\u7684\u6808\u4E2D\u91CD\u65B0\u88C5\u5165CPU\u5BC4\u5B58\u5668, \u5E76\u5F00\u59CB\u4E0B\u4E00\u4E2A\u4EFB\u52A1\u7684\u8FD0\u884C, \u8FD9\u4E00\u8FC7\u7A0B\u5C31\u662Fcontext switch\u3002</p></blockquote><p>\u6982\u62EC\u6765\u8BF4\u5C31\u662F\uFF1A\u5F53\u524D\u4EFB\u52A1\u5728\u6267\u884C\u5B8C CPU \u65F6\u95F4\u7247\u5207\u6362\u5230\u53E6\u4E00\u4E2A\u4EFB\u52A1\u4E4B\u524D\u4F1A\u5148\u4FDD\u5B58\u81EA\u5DF1\u7684\u72B6\u6001\uFF0C\u4EE5\u4FBF\u4E0B\u6B21\u518D\u5207\u6362\u56DE\u8FD9\u4E2A\u4EFB\u52A1\u65F6\uFF0C\u53EF\u4EE5\u518D\u52A0\u8F7D\u8FD9\u4E2A\u4EFB\u52A1\u7684\u72B6\u6001\u3002<strong>\u4EFB\u52A1\u4ECE\u4FDD\u5B58\u5230\u518D\u52A0\u8F7D\u7684\u8FC7\u7A0B\u5C31\u662F\u4E00\u6B21\u4E0A\u4E0B\u6587\u5207\u6362</strong>\u3002</p><p>\u4E0A\u4E0B\u6587\u5207\u6362\u901A\u5E38\u662F\u8BA1\u7B97\u5BC6\u96C6\u578B\u7684\u3002\u4E5F\u5C31\u662F\u8BF4\uFF0C\u5B83\u9700\u8981\u76F8\u5F53\u53EF\u89C2\u7684\u5904\u7406\u5668\u65F6\u95F4\uFF0C\u5728\u6BCF\u79D2\u51E0\u5341\u4E0A\u767E\u6B21\u7684\u5207\u6362\u4E2D\uFF0C\u6BCF\u6B21\u5207\u6362\u90FD\u9700\u8981\u7EB3\u79D2\u91CF\u7EA7\u7684\u65F6\u95F4\u3002\u6240\u4EE5\uFF0C\u4E0A\u4E0B\u6587\u5207\u6362\u5BF9\u7CFB\u7EDF\u6765\u8BF4\u610F\u5473\u7740\u6D88\u8017\u5927\u91CF\u7684 CPU \u65F6\u95F4\uFF0C\u4E8B\u5B9E\u4E0A\uFF0C\u53EF\u80FD\u662F\u64CD\u4F5C\u7CFB\u7EDF\u4E2D\u65F6\u95F4\u6D88\u8017\u6700\u5927\u7684\u64CD\u4F5C\u3002</p><p><strong>Linux</strong> \u76F8\u6BD4\u4E0E\u5176\u4ED6\u64CD\u4F5C\u7CFB\u7EDF\uFF08\u5305\u62EC\u5176\u4ED6\u7C7B Unix \u7CFB\u7EDF\uFF09\u6709\u5F88\u591A\u7684\u4F18\u70B9\uFF0C\u5176\u4E2D\u6709\u4E00\u9879\u5C31\u662F\uFF0C<strong>\u5176\u4E0A\u4E0B\u6587\u5207\u6362\u548C\u6A21\u5F0F\u5207\u6362\u7684\u65F6\u95F4\u6D88\u8017\u975E\u5E38\u5C11</strong>\u3002</p><h2 id="_2-\u5F15\u8D77\u7EBF\u7A0B\u4E0A\u4E0B\u6587\u5207\u6362\u7684\u539F\u56E0" tabindex="-1"><a class="header-anchor" href="#_2-\u5F15\u8D77\u7EBF\u7A0B\u4E0A\u4E0B\u6587\u5207\u6362\u7684\u539F\u56E0" aria-hidden="true">#</a> 2 \u5F15\u8D77\u7EBF\u7A0B\u4E0A\u4E0B\u6587\u5207\u6362\u7684\u539F\u56E0</h2><p>\u5BF9\u4E8E\u6211\u4EEC\u7ECF\u5E38\u4F7F\u7528\u7684\u62A2\u5360\u5F0F\u64CD\u4F5C\u7CFB\u7EDF\u800C\u8A00\uFF0C\u5F15\u8D77\u7EBF\u7A0B\u4E0A\u4E0B\u6587\u5207\u6362\u7684\u539F\u56E0\u5927\u6982\u6709\u4EE5\u4E0B\u51E0\u79CD\uFF1A</p><ul><li>\u5F53\u524D\u6267\u884C\u4EFB\u52A1\u7684\u65F6\u95F4\u7247\u7528\u5B8C\u4E4B\u540E\uFF0C\u7CFB\u7EDFCPU\u6B63\u5E38\u8C03\u5EA6\u4E0B\u4E00\u4E2A\u4EFB\u52A1</li><li>\u5F53\u524D\u6267\u884C\u4EFB\u52A1\u78B0\u5230IO\u963B\u585E\uFF0C\u8C03\u5EA6\u5668\u5C06\u6B64\u4EFB\u52A1\u6302\u8D77\uFF0C\u7EE7\u7EED\u4E0B\u4E00\u4EFB\u52A1</li><li>\u591A\u4E2A\u4EFB\u52A1\u62A2\u5360\u9501\u8D44\u6E90\uFF0C\u5F53\u524D\u4EFB\u52A1\u6CA1\u6709\u62A2\u5230\u9501\u8D44\u6E90\uFF0C\u88AB\u8C03\u5EA6\u5668\u6302\u8D77\uFF0C\u7EE7\u7EED\u4E0B\u4E00\u4EFB\u52A1</li><li>\u7528\u6237\u4EE3\u7801\u6302\u8D77\u5F53\u524D\u4EFB\u52A1\uFF0C\u8BA9\u51FACPU\u65F6\u95F4</li><li>\u786C\u4EF6\u4E2D\u65AD</li></ul><h2 id="_3-\u5982\u4F55\u51CF\u5C11\u4E0A\u4E0B\u6587\u5207\u6362" tabindex="-1"><a class="header-anchor" href="#_3-\u5982\u4F55\u51CF\u5C11\u4E0A\u4E0B\u6587\u5207\u6362" aria-hidden="true">#</a> 3 \u5982\u4F55\u51CF\u5C11\u4E0A\u4E0B\u6587\u5207\u6362</h2><p>\u65E2\u7136\u4E0A\u4E0B\u6587\u5207\u6362\u4F1A\u5BFC\u81F4\u989D\u5916\u7684\u5F00\u9500\uFF0C\u56E0\u6B64\u51CF\u5C11\u4E0A\u4E0B\u6587\u5207\u6362\u6B21\u6570\u4FBF\u53EF\u4EE5\u63D0\u9AD8\u591A\u7EBF\u7A0B\u7A0B\u5E8F\u7684\u8FD0\u884C\u6548\u7387\u3002\u51CF\u5C11\u4E0A\u4E0B\u6587\u5207\u6362\u7684\u65B9\u6CD5\u6709\u65E0\u9501\u5E76\u53D1\u7F16\u7A0B\u3001CAS\u7B97\u6CD5\u3001\u4F7F\u7528\u6700\u5C11\u7EBF\u7A0B\u548C\u4F7F\u7528\u534F\u7A0B\u3002</p><ul><li>\u65E0\u9501\u5E76\u53D1\u7F16\u7A0B\u3002\u591A\u7EBF\u7A0B\u7ADE\u4E89\u65F6\uFF0C\u4F1A\u5F15\u8D77\u4E0A\u4E0B\u6587\u5207\u6362\uFF0C\u6240\u4EE5\u591A\u7EBF\u7A0B\u5904\u7406\u6570\u636E\u65F6\uFF0C\u53EF\u4EE5\u7528\u4E00\u4E9B\u529E\u6CD5\u6765\u907F\u514D\u4F7F\u7528\u9501\uFF0C\u5982\u5C06\u6570\u636E\u7684ID\u6309\u7167Hash\u53D6\u6A21\u5206\u6BB5\uFF0C\u4E0D\u540C\u7684\u7EBF\u7A0B\u5904\u7406\u4E0D\u540C\u6BB5\u7684\u6570\u636E</li><li>CAS\u7B97\u6CD5\u3002Java\u7684Atomic\u5305\u4F7F\u7528CAS\u7B97\u6CD5\u6765\u66F4\u65B0\u6570\u636E\uFF0C\u800C\u4E0D\u9700\u8981\u52A0\u9501</li><li>\u4F7F\u7528\u6700\u5C11\u7EBF\u7A0B\u3002\u907F\u514D\u521B\u5EFA\u4E0D\u9700\u8981\u7684\u7EBF\u7A0B\uFF0C\u6BD4\u5982\u4EFB\u52A1\u5F88\u5C11\uFF0C\u4F46\u662F\u521B\u5EFA\u4E86\u5F88\u591A\u7EBF\u7A0B\u6765\u5904\u7406\uFF0C\u8FD9\u6837\u4F1A\u9020\u6210\u5927\u91CF\u7EBF\u7A0B\u90FD\u5904\u4E8E\u7B49\u5F85\u72B6\u6001</li><li>\u534F\u7A0B\u3002\u5728\u5355\u7EBF\u7A0B\u91CC\u5B9E\u73B0\u591A\u4EFB\u52A1\u7684\u8C03\u5EA6\uFF0C\u5E76\u5728\u5355\u7EBF\u7A0B\u91CC\u7EF4\u6301\u591A\u4E2A\u4EFB\u52A1\u95F4\u7684\u5207\u6362</li></ul>',14),l=[o];function n(s,c){return t(),i("div",null,l)}const h=e(r,[["render",n],["__file","\u591A\u7EBF\u7A0B\u7F16\u7A0B\u4E2D\u7684\u4E0A\u4E0B\u6587\u5207\u6362.html.vue"]]);export{h as default};
